"use client";

import { cn } from "@/lib/utils";
import type { HTMLMotionProps, Variants } from "motion/react";
import { motion, useAnimation, useReducedMotion } from "motion/react";
import { forwardRef, useCallback, useImperativeHandle, useRef } from "react";

export interface LayoutListIconHandle {
 startAnimation: () => void;
 stopAnimation: () => void;
}

interface LayoutListIconProps extends HTMLMotionProps<"div"> {
 size?: number;
 duration?: number;
 isAnimated?: boolean;
}

const LayoutListIcon = forwardRef<LayoutListIconHandle, LayoutListIconProps>(
 (
  {
   className,
   size = 24,
   duration = 1,
   isAnimated = true,
   onMouseEnter,
   onMouseLeave,
   ...props
  },
  ref,
 ) => {
  const controls = useAnimation();
  const reduced = useReducedMotion();
  const isControlled = useRef(false);

  useImperativeHandle(ref, () => {
   isControlled.current = true;
   return {
    startAnimation: () =>
     reduced ? controls.start("normal") : controls.start("animate"),
    stopAnimation: () => controls.start("normal"),
   };
  });

  const handleEnter = useCallback(
   (e?: React.MouseEvent<HTMLDivElement>) => {
    if (!isAnimated || reduced) return;
    if (!isControlled.current) controls.start("animate");
    else onMouseEnter?.(e as any);
   },
   [controls, reduced, isAnimated, onMouseEnter],
  );

  const handleLeave = useCallback(
   (e: React.MouseEvent<HTMLDivElement>) => {
    if (!isControlled.current) controls.start("normal");
    else onMouseLeave?.(e as any);
   },
   [controls, onMouseLeave],
  );

  const itemVariant: Variants = {
   normal: { opacity: 1, x: 0 },
   animate: {
    opacity: [0, 1],
    x: [-6, 0],
    transition: { duration: 0.55 * duration },
   },
  };

  const lineVariant: Variants = {
   normal: { pathLength: 1 },
   animate: {
    pathLength: [0, 1],
    transition: { duration: 0.55 * duration },
   },
  };

  return (
   <motion.div
    className={cn("inline-flex items-center justify-center", className)}
    onMouseEnter={handleEnter}
    onMouseLeave={handleLeave}
    {...props}
   >
    <motion.svg
     xmlns="http://www.w3.org/2000/svg"
     width={size}
     height={size}
     viewBox="0 0 24 24"
     fill="none"
     stroke="currentColor"
     strokeWidth="2"
     strokeLinecap="round"
     strokeLinejoin="round"
     animate={controls}
     initial="normal"
    >
     <motion.rect
      width="7"
      height="7"
      x="3"
      y="3"
      rx="1"
      variants={itemVariant}
     />
     <motion.rect
      width="7"
      height="7"
      x="3"
      y="14"
      rx="1"
      variants={itemVariant}
     />
     <motion.path d="M14 4h7" variants={lineVariant} />
     <motion.path d="M14 9h7" variants={lineVariant} />
     <motion.path d="M14 15h7" variants={lineVariant} />
     <motion.path d="M14 20h7" variants={lineVariant} />
    </motion.svg>
   </motion.div>
  );
 },
);

LayoutListIcon.displayName = "LayoutListIcon";
export { LayoutListIcon };
