"use client";

import { cn } from "@/lib/utils";
import type { HTMLMotionProps, Variants } from "motion/react";
import { motion, useAnimation, useReducedMotion } from "motion/react";
import { forwardRef, useCallback, useImperativeHandle, useRef } from "react";

export interface CodeHandle {
 startAnimation: () => void;
 stopAnimation: () => void;
}

interface CodeProps extends HTMLMotionProps<"div"> {
 size?: number;
 duration?: number;
 isAnimated?: boolean;
}

const CodeIcon = forwardRef<CodeHandle, CodeProps>(
 (
  {
   onMouseEnter,
   onMouseLeave,
   className,
   size = 24,
   duration = 1,
   isAnimated = true,
   ...props
  },
  ref,
 ) => {
  const controls = useAnimation();
  const reduced = useReducedMotion();
  const isControlled = useRef(false);

  useImperativeHandle(ref, () => {
   isControlled.current = true;
   return {
    startAnimation: () =>
     reduced ? controls.start("normal") : controls.start("animate"),
    stopAnimation: () => controls.start("normal"),
   };
  });

  const handleEnter = useCallback(
   (e?: React.MouseEvent<HTMLDivElement>) => {
    if (!isAnimated || reduced) return;
    if (!isControlled.current) controls.start("animate");
    else onMouseEnter?.(e as any);
   },
   [controls, reduced, isAnimated, onMouseEnter],
  );

  const handleLeave = useCallback(
   (e?: React.MouseEvent<HTMLDivElement>) => {
    if (!isControlled.current) controls.start("normal");
    else onMouseLeave?.(e as any);
   },
   [controls, onMouseLeave],
  );

  const groupVariants: Variants = {
   normal: {
    scaleX: 1,
   },
   animate: {
    scaleX: [1, 0.75, 1],
    transition: {
     duration: 0.28 * duration,
     ease: "easeOut",
    },
   },
  };

  const leftBracket: Variants = {
   normal: {
    x: 0,
    opacity: 1,
   },
   animate: {
    x: [0, 3, 0],
    opacity: [1, 0.6, 1],
    transition: {
     duration: 0.32 * duration,
     ease: "easeOut",
    },
   },
  };

  const rightBracket: Variants = {
   normal: {
    x: 0,
    opacity: 1,
   },
   animate: {
    x: [0, -3, 0],
    opacity: [1, 0.6, 1],
    transition: {
     duration: 0.32 * duration,
     delay: 0.05 * duration,
     ease: "easeOut",
    },
   },
  };

  return (
   <motion.div
    className={cn("inline-flex items-center justify-center", className)}
    onMouseEnter={handleEnter}
    onMouseLeave={handleLeave}
    {...props}
   >
    <motion.svg
     xmlns="http://www.w3.org/2000/svg"
     width={size}
     height={size}
     viewBox="0 0 24 24"
     fill="none"
     stroke="currentColor"
     strokeWidth="2"
     strokeLinecap="round"
     strokeLinejoin="round"
    >
     <motion.g variants={groupVariants} initial="normal" animate={controls}>
      <motion.path
       d="m8 6-6 6 6 6"
       variants={leftBracket}
       initial="normal"
       animate={controls}
      />
      <motion.path
       d="m16 18 6-6-6-6"
       variants={rightBracket}
       initial="normal"
       animate={controls}
      />
     </motion.g>
    </motion.svg>
   </motion.div>
  );
 },
);

CodeIcon.displayName = "CodeIcon";
export { CodeIcon };
