"use client";

import type { HTMLMotionProps, Variants } from "motion/react";
import { motion, useAnimation, useReducedMotion } from "motion/react";
import { forwardRef, useCallback, useImperativeHandle, useRef } from "react";
import { cn } from "../../lib/utils";

export interface MenuIconHandle {
 startAnimation: () => void;
 stopAnimation: () => void;
}

interface MenuIconProps extends HTMLMotionProps<"div"> {
 size?: number;
 duration?: number;
 isAnimated?: boolean;
}

const MenuIcon = forwardRef<MenuIconHandle, MenuIconProps>(
 (
  {
   onMouseEnter,
   onMouseLeave,
   className,
   size = 24,
   duration = 1,
   isAnimated = true,
   ...props
  },
  ref,
 ) => {
  const controls = useAnimation();
  const reduced = useReducedMotion();
  const isControlled = useRef(false);

  useImperativeHandle(ref, () => {
   isControlled.current = true;
   return {
    startAnimation: () =>
     reduced ? controls.start("normal") : controls.start("animate"),
    stopAnimation: () => controls.start("normal"),
   };
  });

  const handleEnter = useCallback(
   (e: React.MouseEvent<HTMLDivElement>) => {
    if (!isAnimated || reduced) return;
    if (!isControlled.current) controls.start("animate");
    else onMouseEnter?.(e as any);
   },
   [controls, reduced, isAnimated, onMouseEnter],
  );

  const handleLeave = useCallback(
   (e: React.MouseEvent<HTMLDivElement>) => {
    if (!isControlled.current) controls.start("normal");
    else onMouseLeave?.(e);
   },
   [controls, onMouseLeave],
  );

  const topVariants: Variants = {
   normal: { y: 0, x: 0 },
   animate: {
    y: -1.5,
    x: 1.5,
    transition: {
     duration: 0.25 * duration,
     ease: "easeOut",
    },
   },
  };

  const midVariants: Variants = {
   normal: { scaleX: 1, opacity: 1 },
   animate: {
    scaleX: 0.7,
    opacity: 0.7,
    transition: {
     duration: 0.2 * duration,
     ease: "easeInOut",
    },
   },
  };

  const bottomVariants: Variants = {
   normal: { y: 0, x: 0 },
   animate: {
    y: 1.5,
    x: -1.5,
    transition: {
     duration: 0.25 * duration,
     ease: "easeOut",
    },
   },
  };

  return (
   <motion.div
    className={cn("inline-flex items-center justify-center", className)}
    onMouseEnter={handleEnter}
    onMouseLeave={handleLeave}
    {...props}
   >
    <motion.svg
     xmlns="http://www.w3.org/2000/svg"
     width={size}
     height={size}
     viewBox="0 0 24 24"
     fill="none"
     stroke="currentColor"
     strokeWidth="2"
     strokeLinecap="round"
     strokeLinejoin="round"
     animate={controls}
     initial="normal"
    >
     <motion.path d="M4 6h16" variants={topVariants} />
     <motion.path d="M4 12h16" variants={midVariants} />
     <motion.path d="M4 18h16" variants={bottomVariants} />
    </motion.svg>
   </motion.div>
  );
 },
);

MenuIcon.displayName = "MenuIcon";
export { MenuIcon };
